import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const ConsoleContainer = styled.div`
  background-color: #1e1e1e;
  border-radius: 5px;
  padding: 10px;
  margin: 10px;
  height: 300px;
  display: flex;
  flex-direction: column;
`;

const ConsoleOutput = styled.div`
  color: #ffffff;
  font-family: monospace;
  flex-grow: 1;
  overflow-y: auto;
  padding: 10px;
  white-space: pre-wrap;
  background-color: #2d2d2d;
  margin-bottom: 10px;
  border-radius: 3px;
`;

const ConsoleInput = styled.textarea`
  background-color: #2d2d2d;
  color: #ffffff;
  font-family: monospace;
  border: 1px solid #3f3f3f;
  border-radius: 3px;
  padding: 5px;
  resize: none;
  height: 60px;
`;

const RunButton = styled.button`
  background-color: #4CAF50;
  color: white;
  padding: 8px 15px;
  border: none;
  border-radius: 3px;
  margin-top: 5px;
  cursor: pointer;
  &:hover {
    background-color: #45a049;
  }
`;

function PythonConsole({ gameState, setGameState }) {
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('Welcome to the Python Console!\nType your Python code and press Run or Ctrl+Enter\n');

  useEffect(() => {
    // Initialize the Python environment
    const initPython = () => {
      if (!window.brython) {
        console.error('Brython not loaded');
        return;
      }

      try {
        // Define the farm object and utility functions in Python
        const setupCode = `
from browser import window, console
import sys

# Create a proxy for the game state
class Farm:
    def __init__(self):
        self.money = ${gameState.money}
        self.inventory = ${JSON.stringify(gameState.inventory)}
        self.land_size = 5
    
    def plow(self, x, y):
        if 0 <= x < 5 and 0 <= y < 5:
            window.farm_action('plow', x, y)
            return True
        return False
    
    def plant(self, x, y, crop_type):
        if 0 <= x < 5 and 0 <= y < 5:
            window.farm_action('plant', x, y, crop_type)
            return True
        return False
    
    def harvest(self, x, y):
        if 0 <= x < 5 and 0 <= y < 5:
            window.farm_action('harvest', x, y)
            return True
        return False
    
    def is_crop_ready(self, x, y):
        if 0 <= x < 5 and 0 <= y < 5:
            return window.farm_action('is_crop_ready', x, y)
        return False
    
    def sell_crop(self, crop_type, amount):
        return window.farm_action('sell_crop', crop_type, amount)
    
    def buy_seeds(self, crop_type, amount):
        return window.farm_action('buy_seeds', crop_type, amount)

# Create the farm instance
farm = Farm()

# Override print to write to our console
def custom_print(*args, sep=' ', end='\\n'):
    text = sep.join(str(arg) for arg in args) + end
    window.update_console(text)

__builtins__.print = custom_print
`;
        // Execute the setup code using eval
        eval(window.brython({debug: 1}));
        eval(`
          var $module = {};
          ${window.pythonToJS(setupCode)}
        `);
      } catch (error) {
        setOutput(prev => prev + '\nError setting up Python environment: ' + error.message + '\n');
      }
    };

    // Initialize when component mounts
    initPython();

    // Add the farm actions to the window object
    window.farm_action = (action, ...args) => {
      try {
        switch (action) {
          case 'plow':
            const [x, y] = args;
            if (x >= 0 && x < 5 && y >= 0 && y < 5) {
              const newLand = [...gameState.land];
              newLand[y * 5 + x] = { ...newLand[y * 5 + x], isPlowed: true };
              setGameState(prev => ({ ...prev, land: newLand }));
              return true;
            }
            return false;

          case 'plant':
            const [plantX, plantY, cropType] = args;
            if (plantX >= 0 && plantX < 5 && plantY >= 0 && plantY < 5) {
              const index = plantY * 5 + plantX;
              if (gameState.land[index].isPlowed && !gameState.land[index].hasCrop) {
                const newLand = [...gameState.land];
                newLand[index] = {
                  ...newLand[index],
                  hasCrop: true,
                  cropType: cropType,
                  cropState: 'growing',
                  plantedTime: Date.now()
                };
                setGameState(prev => ({ ...prev, land: newLand }));
                return true;
              }
            }
            return false;

          case 'harvest':
            const [harvestX, harvestY] = args;
            if (harvestX >= 0 && harvestX < 5 && harvestY >= 0 && harvestY < 5) {
              const index = harvestY * 5 + harvestX;
              if (gameState.land[index].hasCrop && gameState.land[index].cropState === 'ready') {
                const cropType = gameState.land[index].cropType;
                const newLand = [...gameState.land];
                newLand[index] = {
                  ...newLand[index],
                  hasCrop: false,
                  cropType: null,
                  cropState: null,
                  plantedTime: null
                };
                setGameState(prev => ({
                  ...prev,
                  land: newLand,
                  inventory: {
                    ...prev.inventory,
                    [cropType]: (prev.inventory[cropType] || 0) + 1
                  }
                }));
                return true;
              }
            }
            return false;

          case 'is_crop_ready':
            const [checkX, checkY] = args;
            if (checkX >= 0 && checkX < 5 && checkY >= 0 && checkY < 5) {
              const tile = gameState.land[checkY * 5 + checkX];
              return tile.hasCrop && tile.cropState === 'ready';
            }
            return false;

          case 'sell_crop':
            const [sellCropType, amount] = args;
            const available = gameState.inventory[sellCropType] || 0;
            if (available >= amount) {
              const prices = { wheat: 20, corn: 30, potato: 40 };
              const price = prices[sellCropType] || 0;
              setGameState(prev => ({
                ...prev,
                money: prev.money + (price * amount),
                inventory: {
                  ...prev.inventory,
                  [sellCropType]: prev.inventory[sellCropType] - amount
                }
              }));
              return true;
            }
            return false;

          case 'buy_seeds':
            const [buyCropType, buyAmount] = args;
            const prices = { wheat: 10, corn: 15, potato: 20 };
            const price = (prices[buyCropType] || 0) * buyAmount;
            if (gameState.money >= price) {
              setGameState(prev => ({
                ...prev,
                money: prev.money - price,
                inventory: {
                  ...prev.inventory,
                  [`${buyCropType}_seeds`]: (prev.inventory[`${buyCropType}_seeds`] || 0) + buyAmount
                }
              }));
              return true;
            }
            return false;

          default:
            return false;
        }
      } catch (error) {
        console.error('Error in farm_action:', error);
        return false;
      }
    };

    // Add the console update function to the window object
    window.update_console = (text) => {
      setOutput(prev => prev + text);
    };
  }, [gameState, setGameState]);

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && e.ctrlKey) {
      e.preventDefault();
      handleRun();
    }
  };

  const handleRun = () => {
    if (!input.trim()) return;

    try {
      // Add the input to the output history
      setOutput(prev => prev + '\n>>> ' + input + '\n');

      // Convert Python code to JavaScript and execute it
      const jsCode = window.pythonToJS(input);
      eval(`
        var $module = {};
        ${jsCode}
      `);
      
      // Clear the input
      setInput('');
    } catch (error) {
      setOutput(prev => prev + 'Error: ' + error.message + '\n');
    }
  };

  return (
    <ConsoleContainer>
      <ConsoleOutput>
        {output}
      </ConsoleOutput>
      <ConsoleInput
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Enter Python code here..."
      />
      <RunButton onClick={handleRun}>Run</RunButton>
    </ConsoleContainer>
  );
}

export default PythonConsole;
