import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Instructions from './Instructions';
import PythonConsole from './PythonConsole';
import TechTree from './TechTree';

const GameContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const MoneyDisplay = styled.div`
  font-size: 1.5em;
  font-weight: bold;
  color: #f1c40f;
  text-align: center;
  padding: 10px;
  background-color: #34495e;
  border-radius: 5px;
`;

const GameGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(${props => props.size}, 1fr);
  gap: 10px;
  width: 90%;
  max-width: 700px;
  margin: 0 auto;
`;

const LandTile = styled.div`
  width: 100%;
  aspect-ratio: 1;
  background-color: ${props => {
    if (props.isPlowed) return '#8B4513';
    if (props.hasCrop && props.cropState === 'growing') return '#90EE90';
    if (props.hasCrop && props.cropState === 'ready') return '#228B22';
    return '#90EE90';
  }};
  border: 1px solid #2c3e50;
  cursor: pointer;
  position: relative;
  transition: all 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const CropIndicator = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 80%;
  height: 80%;
  background-color: ${props => props.color};
  border-radius: 50%;
`;

const GameStats = styled.div`
  position: fixed;
  top: 20px;
  right: 320px;
  background-color: rgba(52, 73, 94, 0.9);
  padding: 10px;
  border-radius: 5px;
`;

const ToolBar = styled.div`
  display: flex;
  margin-bottom: 20px;
  flex-wrap: wrap;
`;

const ToolButton = styled.button`
  background-color: ${props => props.selected ? '#2980b9' : '#3498db'};
  border: ${props => props.selected ? '2px solid #fff' : 'none'};
  padding: ${props => props.selected ? '6px 14px' : '8px 16px'};
`;

const CropSelector = styled.div`
  margin-top: 10px;
  display: flex;
  flex-wrap: wrap;
`;

const CropButton = styled.button`
  background-color: ${props => props.selected ? '#27ae60' : '#2ecc71'};
  display: flex;
  align-items: center;
  justify-content: space-between;
  
  &:disabled {
    background-color: #95a5a6;
    cursor: not-allowed;
  }
`;

const CropIcon = styled.span`
  display: inline-block;
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background-color: ${props => props.color};
  margin-right: 5px;
`;

const GameLayout = styled.div`
  display: grid;
  grid-template-columns: 1fr 300px;
  gap: 20px;
  padding: 20px;
`;

const MainArea = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

function Game({ inventory, setInventory, money, setMoney }) {
  const [gridSize, setGridSize] = useState(1);
  const [land, setLand] = useState(Array(1).fill(null).map(() => ({ 
    isPlowed: false, 
    hasCrop: false, 
    cropType: null,
    cropState: null,
    plantedTime: null
  })));
  
  const [selectedTool, setSelectedTool] = useState('plow');
  const [selectedCrop, setSelectedCrop] = useState('wheat');
  const [showInstructions, setShowInstructions] = useState(false);
  const [showTechTree, setShowTechTree] = useState(false);

  // Crop growth timer
  useEffect(() => {
    const growthInterval = setInterval(() => {
      let updated = false;
      const newLand = [...land];
      
      newLand.forEach((tile, index) => {
        if (tile.hasCrop && tile.cropState === 'growing' && tile.plantedTime) {
          const now = Date.now();
          const growthTime = 10000; // 10 seconds for growth
          
          if (now - tile.plantedTime > growthTime) {
            newLand[index] = {
              ...tile,
              cropState: 'ready'
            };
            updated = true;
          }
        }
      });
      
      if (updated) {
        setLand(newLand);
      }
    }, 1000);
    
    return () => clearInterval(growthInterval);
  }, [land]);

  const cropColors = {
    wheat: '#f1c40f',
    corn: '#f39c12',
    potato: '#d35400'
  };

  const gameState = {
    money,
    inventory,
    land,
    selectedTool,
    selectedCrop
  };

  const setGameState = (newState) => {
    if (typeof newState === 'function') {
      const updatedState = newState({ money, inventory, land });
      setMoney(updatedState.money);
      setInventory(updatedState.inventory);
      setLand(updatedState.land);
    } else {
      setMoney(newState.money);
      setInventory(newState.inventory);
      setLand(newState.land);
    }
  };

  const handleTileClick = (index) => {
    const x = index % gridSize;
    const y = Math.floor(index / gridSize);
    const tile = land[index];

    switch (selectedTool) {
      case 'plow':
        if (!tile.isPlowed && !tile.hasCrop) {
          const newLand = [...land];
          newLand[index] = { ...tile, isPlowed: true };
          setLand(newLand);
        }
        break;
      case 'plant':
        if (tile.isPlowed && !tile.hasCrop) {
          const newLand = [...land];
          newLand[index] = {
            ...tile,
            hasCrop: true,
            cropType: selectedCrop,
            cropState: 'growing',
            plantedTime: Date.now()
          };
          setLand(newLand);
        }
        break;
      case 'harvest':
        if (tile.hasCrop && tile.cropState === 'ready') {
          const newLand = [...land];
          newLand[index] = {
            ...tile,
            hasCrop: false,
            cropType: null,
            cropState: null,
            plantedTime: null
          };
          setLand(newLand);
          setInventory({
            ...inventory,
            [tile.cropType]: (inventory[tile.cropType] || 0) + 1
          });
        }
        break;
      default:
        break;
    }
  };

  const sellAllCrops = () => {
    const prices = {
      wheat: 20,
      corn: 30,
      potato: 40
    };

    let totalEarnings = 0;
    const newInventory = { ...inventory };

    Object.entries(inventory).forEach(([crop, amount]) => {
      if (prices[crop]) {
        totalEarnings += prices[crop] * amount;
        newInventory[crop] = 0;
      }
    });

    setMoney(money + totalEarnings);
    setInventory(newInventory);
  };

  const handleTechUnlock = (techId) => {
    if (techId === 'expand') {
      // Handle farm expansion
      if (gridSize < 5) {
        const newSize = gridSize + 1;
        const newLand = Array(newSize * newSize).fill(null).map((_, index) => {
          if (index < land.length) {
            return land[index];
          }
          return {
            isPlowed: false,
            hasCrop: false,
            cropType: null,
            cropState: null,
            plantedTime: null
          };
        });
        setGridSize(newSize);
        setLand(newLand);
      }
    }
    // Add other tech effects here
  };

  return (
    <GameLayout>
      <MainArea>
        <MoneyDisplay>
          Money: ${money}
        </MoneyDisplay>
        
        <GameStats>
          <div>Land Size: {gridSize}x{gridSize}</div>
          <button onClick={sellAllCrops}>Sell All Crops</button>
          <button onClick={() => setShowTechTree(true)}>Open Tech Tree</button>
        </GameStats>
        
        <Instructions isOpen={showInstructions} onClose={() => setShowInstructions(false)} />
        
        {showTechTree && (
          <TechTree 
            money={money} 
            setMoney={setMoney} 
            onUnlock={handleTechUnlock}
            onClose={() => setShowTechTree(false)}
          />
        )}

        <h2>The Farmer Was Replaced</h2>
        
        <ToolBar>
          <ToolButton 
            selected={selectedTool === 'plow'} 
            onClick={() => setSelectedTool('plow')}
          >
            Plow
          </ToolButton>
          <ToolButton 
            selected={selectedTool === 'plant'} 
            onClick={() => setSelectedTool('plant')}
          >
            Plant
          </ToolButton>
          <ToolButton 
            selected={selectedTool === 'harvest'} 
            onClick={() => setSelectedTool('harvest')}
          >
            Harvest
          </ToolButton>
        </ToolBar>
        
        <CropSelector>
          <div>Select crop to plant:</div>
          {Object.keys(cropColors).map(crop => {
            const seedType = `${crop}_seeds`;
            return (
              <CropButton
                key={crop}
                selected={selectedCrop === crop}
                onClick={() => setSelectedCrop(crop)}
                disabled={!inventory[seedType] || inventory[seedType] <= 0}
              >
                <CropIcon color={cropColors[crop]} />
                {crop.charAt(0).toUpperCase() + crop.slice(1)} ({inventory[seedType] || 0})
              </CropButton>
            );
          })}
        </CropSelector>
        
        <GameGrid size={gridSize}>
          {land.map((tile, index) => (
            <LandTile
              key={index}
              onClick={() => handleTileClick(index)}
              isPlowed={tile.isPlowed}
              hasCrop={tile.hasCrop}
              cropState={tile.cropState}
            >
              {tile.hasCrop && (
                <CropIndicator 
                  color={cropColors[tile.cropType]} 
                />
              )}
            </LandTile>
          ))}
        </GameGrid>
        
        <div style={{ marginTop: '20px', marginBottom: '20px' }}>
          <button onClick={() => setShowInstructions(true)}>Game Instructions</button>
        </div>
      </MainArea>

      <PythonConsole gameState={gameState} setGameState={setGameState} />
    </GameLayout>
  );
}

export default Game;
