import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Instructions from './Instructions';

const GameGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(${props => props.size}, 1fr);
  gap: 5px;
  max-width: 800px;
  margin: 0 auto;
`;

const LandTile = styled.div`
  width: 100%;
  aspect-ratio: 1;
  background-color: ${props => {
    if (props.isPlowed) return '#8B4513';
    if (props.hasCrop && props.cropState === 'growing') return '#90EE90';
    if (props.hasCrop && props.cropState === 'ready') return '#228B22';
    return '#90EE90';
  }};
  border: 1px solid #2c3e50;
  cursor: pointer;
  position: relative;
  transition: all 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const CropIndicator = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 80%;
  height: 80%;
  background-color: ${props => props.color};
  border-radius: 50%;
`;

const GameStats = styled.div`
  position: fixed;
  top: 20px;
  right: 320px;
  background-color: rgba(52, 73, 94, 0.9);
  padding: 10px;
  border-radius: 5px;
`;

const ToolBar = styled.div`
  display: flex;
  margin-bottom: 20px;
  flex-wrap: wrap;
`;

const ToolButton = styled.button`
  background-color: ${props => props.selected ? '#2980b9' : '#3498db'};
  border: ${props => props.selected ? '2px solid #fff' : 'none'};
  padding: ${props => props.selected ? '6px 14px' : '8px 16px'};
`;

const CropSelector = styled.div`
  margin-top: 10px;
  display: flex;
  flex-wrap: wrap;
`;

const CropButton = styled.button`
  background-color: ${props => props.selected ? '#27ae60' : '#2ecc71'};
  display: flex;
  align-items: center;
  justify-content: space-between;
  
  &:disabled {
    background-color: #95a5a6;
    cursor: not-allowed;
  }
`;

const CropIcon = styled.span`
  display: inline-block;
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background-color: ${props => props.color};
  margin-right: 5px;
`;

function Game({ inventory, setInventory }) {
  const [gridSize, setGridSize] = useState(5);
  const [land, setLand] = useState(Array(gridSize * gridSize).fill(null).map(() => ({ 
    isPlowed: false, 
    hasCrop: false, 
    cropType: null,
    cropState: null,
    plantedTime: null
  })));
  
  const [money, setMoney] = useState(100);
  const [selectedTool, setSelectedTool] = useState('plow');
  const [selectedCrop, setSelectedCrop] = useState('wheat');
  const [showInstructions, setShowInstructions] = useState(false);

  // Crop growth timer
  useEffect(() => {
    const growthInterval = setInterval(() => {
      let updated = false;
      const newLand = [...land];
      
      newLand.forEach((tile, index) => {
        if (tile.hasCrop && tile.cropState === 'growing' && tile.plantedTime) {
          const now = Date.now();
          const growthTime = 10000; // 10 seconds for growth
          
          if (now - tile.plantedTime > growthTime) {
            newLand[index] = {
              ...tile,
              cropState: 'ready'
            };
            updated = true;
          }
        }
      });
      
      if (updated) {
        setLand(newLand);
      }
    }, 1000);
    
    return () => clearInterval(growthInterval);
  }, [land]);

  const cropColors = {
    wheat: '#f1c40f',
    corn: '#f39c12',
    potato: '#d35400'
  };

  const handleTileClick = (index) => {
    const newLand = [...land];
    const tile = newLand[index];

    switch (selectedTool) {
      case 'plow':
        if (!tile.isPlowed && !tile.hasCrop) {
          newLand[index] = { ...tile, isPlowed: true };
        }
        break;
        
      case 'plant':
        if (tile.isPlowed && !tile.hasCrop) {
          const seedType = `${selectedCrop}_seeds`;
          
          if (inventory[seedType] > 0) {
            newLand[index] = { 
              ...tile, 
              hasCrop: true, 
              cropType: selectedCrop,
              cropState: 'growing',
              plantedTime: Date.now()
            };
            
            setInventory({
              ...inventory,
              [seedType]: inventory[seedType] - 1
            });
          } else {
            alert(`You don't have any ${selectedCrop} seeds!`);
          }
        }
        break;
        
      case 'harvest':
        if (tile.hasCrop && tile.cropState === 'ready') {
          newLand[index] = { 
            ...tile, 
            hasCrop: false, 
            cropType: null,
            cropState: null,
            plantedTime: null,
            isPlowed: false
          };
          
          setInventory({
            ...inventory,
            [tile.cropType]: (inventory[tile.cropType] || 0) + 1
          });
        }
        break;
        
      default:
        break;
    }

    setLand(newLand);
  };

  const toggleInstructions = () => {
    setShowInstructions(!showInstructions);
  };

  const sellAllCrops = () => {
    let profit = 0;
    const prices = {
      wheat: 20,
      corn: 40,
      potato: 30
    };
    
    const newInventory = {...inventory};
    
    Object.keys(prices).forEach(crop => {
      if (newInventory[crop] > 0) {
        profit += prices[crop] * newInventory[crop];
        newInventory[crop] = 0;
      }
    });
    
    setMoney(prev => prev + profit);
    setInventory(newInventory);
    
    if (profit > 0) {
      alert(`Sold all crops for $${profit}!`);
    } else {
      alert('No crops to sell!');
    }
  };

  const buySeedsWithType = (seedType, price) => {
    if (money >= price) {
      setMoney(prev => prev - price);
      setInventory({
        ...inventory,
        [seedType]: (inventory[seedType] || 0) + 1
      });
    } else {
      alert('Not enough money!');
    }
  };

  const cropKeyToName = {
    wheat_seeds: 'Wheat Seeds',
    corn_seeds: 'Corn Seeds',
    potato_seeds: 'Potato Seeds'
  };

  return (
    <div>
      <GameStats>
        <div>Money: ${money}</div>
        <div>Land Size: {gridSize}x{gridSize}</div>
        <button onClick={sellAllCrops}>Sell All Crops</button>
      </GameStats>
      
      <Instructions isOpen={showInstructions} onClose={toggleInstructions} />
      
      <h2>The Farmer Was Replaced</h2>
      
      <ToolBar>
        <ToolButton 
          selected={selectedTool === 'plow'} 
          onClick={() => setSelectedTool('plow')}
        >
          Plow
        </ToolButton>
        <ToolButton 
          selected={selectedTool === 'plant'} 
          onClick={() => setSelectedTool('plant')}
        >
          Plant
        </ToolButton>
        <ToolButton 
          selected={selectedTool === 'harvest'} 
          onClick={() => setSelectedTool('harvest')}
        >
          Harvest
        </ToolButton>
      </ToolBar>
      
      {selectedTool === 'plant' && (
        <CropSelector>
          <div>Select crop to plant:</div>
          {Object.keys(cropColors).map(crop => {
            const seedType = `${crop}_seeds`;
            return (
              <CropButton
                key={crop}
                selected={selectedCrop === crop}
                onClick={() => setSelectedCrop(crop)}
                disabled={!inventory[seedType] || inventory[seedType] <= 0}
              >
                <CropIcon color={cropColors[crop]} />
                {crop.charAt(0).toUpperCase() + crop.slice(1)} ({inventory[seedType] || 0})
              </CropButton>
            );
          })}
        </CropSelector>
      )}
      
      <GameGrid size={gridSize}>
        {land.map((tile, index) => (
          <LandTile
            key={index}
            isPlowed={tile.isPlowed}
            hasCrop={tile.hasCrop}
            cropState={tile.cropState}
            onClick={() => handleTileClick(index)}
          >
            {tile.hasCrop && (
              <CropIndicator 
                color={cropColors[tile.cropType]} 
              />
            )}
          </LandTile>
        ))}
      </GameGrid>
      
      <div style={{ marginTop: '20px', marginBottom: '20px' }}>
        <button onClick={toggleInstructions}>Game Instructions</button>
      </div>
    </div>
  );
}

export default Game;
