import React, { useRef, useEffect, useState, useLayoutEffect } from 'react';
import * as THREE from 'three';

const Game3D = ({ gameState, setGameState }) => {
  const [isInitialized, setIsInitialized] = useState(false);
  const [isMounted, setIsMounted] = useState(false);
  const canvasRef = useRef(null);
  const sceneRef = useRef(null);
  const cameraRef = useRef(null);
  const rendererRef = useRef(null);
  const droneRef = useRef(null);
  const modelsRef = useRef({});
  const animationFrameRef = useRef(null);

  // Ensure component is mounted before initialization
  useLayoutEffect(() => {
    setIsMounted(true);
    return () => setIsMounted(false);
  }, []);

  // Initialize Three.js scene
  const initializeScene = () => {
    try {
      if (!canvasRef.current || !isMounted) {
        console.warn('Canvas element not found or component not mounted');
        return false;
      }

      // Create scene
      const scene = new THREE.Scene();
      scene.background = new THREE.Color(0x87CEEB);
      
      // Create camera
      const camera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
      camera.position.set(5, 5, 5);
      camera.lookAt(0, 0, 0);
      
      // Create renderer
      const renderer = new THREE.WebGLRenderer({ 
        canvas: canvasRef.current,
        antialias: true,
        alpha: true
      });
      renderer.setSize(600, 600, false);

      // Add lights
      const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
      scene.add(ambientLight);
      
      const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
      directionalLight.position.set(10, 10, 5);
      scene.add(directionalLight);

      // Create ground plane
      const groundGeometry = new THREE.PlaneGeometry(5, 5);
      const groundMaterial = new THREE.MeshStandardMaterial({ 
        color: 0x8B4513,
        roughness: 0.8,
        metalness: 0.2
      });
      const ground = new THREE.Mesh(groundGeometry, groundMaterial);
      ground.rotation.x = -Math.PI / 2;
      scene.add(ground);

      // Create grid helper
      const gridHelper = new THREE.GridHelper(5, 5);
      scene.add(gridHelper);

      // Create drone using basic geometry
      const droneGeometry = new THREE.BoxGeometry(0.4, 0.2, 0.4);
      const droneMaterial = new THREE.MeshStandardMaterial({ color: 0x3498db });
      const drone = new THREE.Mesh(droneGeometry, droneMaterial);
      drone.position.set(0, 0.5, 0);
      scene.add(drone);
      droneRef.current = drone;

      // Create crop geometries
      const cropGeometries = {
        wheat: new THREE.CylinderGeometry(0.1, 0.1, 0.5, 8),
        corn: new THREE.CylinderGeometry(0.1, 0.1, 0.8, 8),
        potato: new THREE.SphereGeometry(0.2, 8, 8)
      };

      const cropMaterials = {
        wheat: new THREE.MeshStandardMaterial({ color: 0xF4D03F }),
        corn: new THREE.MeshStandardMaterial({ color: 0xF39C12 }),
        potato: new THREE.MeshStandardMaterial({ color: 0x935116 })
      };

      // Create crop instances
      const crops = {};
      Object.entries(cropGeometries).forEach(([cropType, geometry]) => {
        const crop = new THREE.Mesh(geometry, cropMaterials[cropType]);
        crop.visible = false;
        scene.add(crop);
        crops[cropType] = crop;
      });

      // Store references
      sceneRef.current = scene;
      cameraRef.current = camera;
      rendererRef.current = renderer;
      modelsRef.current = crops;

      return true;
    } catch (error) {
      console.error('Error initializing scene:', error);
      return false;
    }
  };

  // Animation loop
  const startAnimation = () => {
    if (!isMounted) return;

    const animate = () => {
      if (!isMounted) return;
      
      animationFrameRef.current = requestAnimationFrame(animate);
      
      // Animate drone
      if (droneRef.current) {
        droneRef.current.rotation.y += 0.02;
        droneRef.current.position.y = 0.5 + Math.sin(Date.now() * 0.002) * 0.1;
      }

      // Animate crops
      gameState.land.forEach((tile, index) => {
        if (tile.hasCrop && modelsRef.current[tile.cropType]) {
          const model = modelsRef.current[tile.cropType];
          if (tile.cropState === 'growing') {
            const scale = 0.3 + Math.sin(Date.now() * 0.001 + index) * 0.1;
            model.scale.set(scale, scale, scale);
          } else if (tile.cropState === 'ready') {
            model.rotation.y += 0.01;
          }
        }
      });

      rendererRef.current.render(sceneRef.current, cameraRef.current);
    };
    animate();
  };

  // Initialize everything
  useEffect(() => {
    if (!isMounted) return;

    const initialize = async () => {
      if (isInitialized) return;

      const sceneInitialized = initializeScene();
      if (!sceneInitialized) return;

      startAnimation();
      setIsInitialized(true);
    };

    initialize();

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
      if (rendererRef.current) {
        rendererRef.current.dispose();
      }
    };
  }, [isInitialized, isMounted]);

  // Update game state
  useEffect(() => {
    if (!isInitialized || !isMounted) return;

    if (droneRef.current) {
      droneRef.current.position.set(
        gameState.dronePosition.x - 2,
        0.5,
        gameState.dronePosition.y - 2
      );
    }

    // Reset all crop visibility first
    Object.values(modelsRef.current).forEach(model => {
      model.visible = false;
    });

    // Update crop positions and visibility
    gameState.land.forEach((tile, index) => {
      const x = Math.floor(index / 5);
      const y = index % 5;
      
      if (tile.hasCrop && modelsRef.current[tile.cropType]) {
        const model = modelsRef.current[tile.cropType];
        model.position.set(x - 2, 0.25, y - 2);
        model.visible = true;
        
        // Add growth animation based on crop state
        if (tile.cropState === 'growing') {
          const scale = 0.3 + Math.sin(Date.now() * 0.001) * 0.1;
          model.scale.set(scale, scale, scale);
        } else if (tile.cropState === 'ready') {
          model.scale.set(1, 1, 1);
          model.rotation.y += 0.01;
        }
      }
    });
  }, [gameState, isInitialized, isMounted]);

  if (!isMounted) return null;

  return (
    <div style={{ width: '100%', height: '100%', position: 'relative' }}>
      <canvas 
        ref={canvasRef} 
        style={{ 
          width: '100%', 
          height: '100%',
          display: 'block'
        }} 
      />
    </div>
  );
};

export default Game3D;
