import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const ConsoleContainer = styled.div`
  background-color: #1e1e1e;
  border-radius: 5px;
  padding: 10px;
  margin: 10px;
  height: 300px;
  display: flex;
  flex-direction: column;
`;

const ConsoleOutput = styled.div`
  color: #ffffff;
  font-family: monospace;
  flex-grow: 1;
  overflow-y: auto;
  padding: 10px;
  white-space: pre-wrap;
  background-color: #2d2d2d;
  margin-bottom: 10px;
  border-radius: 3px;
`;

const ConsoleInput = styled.textarea`
  background-color: #2d2d2d;
  color: #ffffff;
  font-family: monospace;
  border: 1px solid #3f3f3f;
  border-radius: 3px;
  padding: 5px;
  resize: none;
  height: 60px;
`;

const RunButton = styled.button`
  background-color: #4CAF50;
  color: white;
  padding: 8px 15px;
  border: none;
  border-radius: 3px;
  margin-top: 5px;
  cursor: pointer;
  &:hover {
    background-color: #45a049;
  }
`;

const PlayPauseButton = styled.button`
  background-color: #4CAF50;
  color: white;
  padding: 8px 15px;
  border: none;
  border-radius: 3px;
  margin-top: 5px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  
  &:hover {
    background-color: #45a049;
  }
  
  &:disabled {
    background-color: #95a5a6;
    cursor: not-allowed;
  }
`;

const PlayIcon = styled.span`
  display: inline-block;
  width: 0;
  height: 0;
  border-style: solid;
  border-width: 10px 0 10px 15px;
  border-color: transparent transparent transparent white;
  margin-left: 3px;
`;

const PauseIcon = styled.span`
  display: flex;
  gap: 3px;
  
  &::before,
  &::after {
    content: '';
    width: 5px;
    height: 20px;
    background-color: white;
  }
`;

// Store console states globally
const consoleStates = {};

function JavaScriptConsole({ gameState, setGameState, consoleId }) {
  const [input, setInput] = useState('');
  const [output, setOutput] = useState(
    'ðŸŒŸ Welcome to the JavaScript Console! ðŸŒŸ\n\n' +
    'ðŸ“ Basic Commands:\n' +
    '  â€¢ move(x, y)         - Move to coordinates (x,y)\n' +
    '  â€¢ scan()             - Get info about current tile\n' +
    '  â€¢ position()         - Get current coordinates\n\n' +
    'ðŸšœ Farming Commands:\n' +
    '  â€¢ plow()            - Plow the current tile\n' +
    '  â€¢ plant("crop")     - Plant a crop (wheat unlocked, others in Tech Tree)\n' +
    '  â€¢ harvest()         - Harvest ready crop at current tile\n\n' +
    'ðŸ—ï¸ Farm Management:\n' +
    '  â€¢ expand()          - Expand farm size (max 5x5)\n' +
    '  â€¢ sell("type", amount) - Sell harvested crops\n' +
    '  â€¢ buy("type", amount)  - Buy seeds for planting\n\n' +
    'ðŸ’¡ Tips:\n' +
    '  â€¢ Start with expand() to increase farm size\n' +
    '  â€¢ Use scan() to check tile status\n' +
    '  â€¢ Crops must be planted on plowed land\n' +
    '  â€¢ Unlock more crops in the Tech Tree\n' +
    '  â€¢ Buy seeds before planting\n\n' +
    'âŒ¨ï¸ Usage:\n' +
    '  â€¢ Type your code and press Play (â–¶) or Ctrl+Enter\n' +
    '  â€¢ Each command runs once when executed\n' +
    '  â€¢ Multiple commands can be entered on separate lines\n\n'
  );
  const [farm, setFarm] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentLine, setCurrentLine] = useState(0);
  const codeLines = useRef([]);
  const executionInterval = useRef(null);

  // Initialize or restore console state
  useEffect(() => {
    if (!consoleStates[consoleId]) {
      consoleStates[consoleId] = {
        input: '',
        output: output,
        farm: null,
        isPlaying: false,
        currentLine: 0
      };
    } else {
      setInput(consoleStates[consoleId].input);
      setOutput(consoleStates[consoleId].output);
      setFarm(consoleStates[consoleId].farm);
      setIsPlaying(consoleStates[consoleId].isPlaying);
      setCurrentLine(consoleStates[consoleId].currentLine);
    }

    // Initialize farm when component mounts
    const newFarm = {
      money: gameState.money,
      inventory: gameState.inventory,
      land: gameState.land,
      size: Math.sqrt(gameState.land.length),
      maxSize: 5,
      drone: gameState.drone,
      
      expand: function() {
        if (this.size >= this.maxSize) {
          this.print(`Farm is already at maximum size (${this.maxSize}x${this.maxSize})`);
          return false;
        }

        const newSize = this.size + 1;
        const newLand = Array(newSize * newSize).fill().map((_, index) => {
          if (index < this.land.length) {
            return this.land[index];
          }
          return {
            isPlowed: false,
            hasCrop: false,
            cropType: null,
            cropState: null,
            plantedTime: null
          };
        });

        setGameState(prev => ({ ...prev, land: newLand }));
        this.land = newLand;
        this.size = newSize;
        this.print(`Farm expanded to ${newSize}x${newSize}`);
        return true;
      },
      
      move: function(x, y) {
        if (0 <= x && x < this.size && 0 <= y && y < this.size) {
          setGameState(prev => ({
            ...prev,
            dronePosition: { x, y },
            drone: { x, y }
          }));
          this.drone = { x, y };
          this.print(`Moved to (${x}, ${y})`);
          return true;
        }
        this.print("Invalid coordinates");
        return false;
      },

      plow: function() {
        const x = this.drone.x;
        const y = this.drone.y;
        const index = y * this.size + x;
        if (this.land[index].isPlowed) {
          this.print("This tile is already plowed");
          return false;
        }
        const newLand = [...this.land];
        newLand[index] = { ...newLand[index], isPlowed: true };
        setGameState(prev => ({ ...prev, land: newLand }));
        this.land = newLand;
        this.print(`Successfully plowed tile at (${x}, ${y})`);
        return true;
      },

      plant: function(cropType) {
        const x = this.drone.x;
        const y = this.drone.y;
        const index = y * this.size + x;
        
        // Check if crop is unlocked
        if (!gameState.unlockedCrops.includes(cropType)) {
          this.print(`Cannot plant ${cropType} - You need to unlock it first in the Tech Tree!`);
          return false;
        }
        
        // Check if we have seeds
        if (!this.inventory[`${cropType}_seeds`] || this.inventory[`${cropType}_seeds`] <= 0) {
          this.print(`Cannot plant ${cropType} - No seeds available!`);
          return false;
        }

        if (this.land[index].isPlowed && !this.land[index].hasCrop) {
          const newLand = [...this.land];
          newLand[index] = {
            ...newLand[index],
            hasCrop: true,
            cropType: cropType,
            cropState: 'growing',
            plantedTime: Date.now()
          };
          
          // Deduct one seed when planting
          const newInventory = {
            ...this.inventory,
            [`${cropType}_seeds`]: this.inventory[`${cropType}_seeds`] - 1
          };
          
          setGameState(prev => ({
            ...prev,
            land: newLand,
            inventory: newInventory
          }));
          
          this.land = newLand;
          this.inventory = newInventory;
          this.print(`Successfully planted ${cropType} at (${x}, ${y})`);
          return true;
        }
        
        this.print(`Failed to plant ${cropType} - Make sure the tile is plowed and empty`);
        return false;
      },

      harvest: function() {
        const x = this.drone.x;
        const y = this.drone.y;
        const index = y * this.size + x;
        
        if (this.land[index].hasCrop && this.land[index].cropState === 'ready') {
          const cropType = this.land[index].cropType;
          const newLand = [...this.land];
          newLand[index] = {
            ...newLand[index],
            hasCrop: false,
            cropType: null,
            cropState: null,
            plantedTime: null
          };
          const newInventory = { ...this.inventory, [cropType]: (this.inventory[cropType] || 0) + 1 };
          setGameState(prev => ({
            ...prev,
            land: newLand,
            inventory: newInventory
          }));
          this.land = newLand;
          this.inventory = newInventory;
          this.print(`Successfully harvested ${cropType} at (${x}, ${y})`);
          return true;
        }
        this.print(`Failed to harvest - No ready crop at (${x}, ${y})`);
        return false;
      },

      scan: function() {
        const x = this.drone.x;
        const y = this.drone.y;
        const index = y * this.size + x;
        const tile = this.land[index];
        
        let status = "empty";
        if (tile.isPlowed) {
          status = "plowed";
        }
        if (tile.hasCrop) {
          status = `${tile.cropState} ${tile.cropType}`;
        }
        
        this.print(`Scan results at (${x}, ${y}): ${status}`);
        return status;
      },

      position: function() {
        return { x: this.drone.x, y: this.drone.y };
      },
      
      sell: function(cropType, amount) {
        const available = this.inventory[cropType] || 0;
        if (available >= amount) {
          const prices = { wheat: 20, corn: 30, potato: 40 };
          const price = prices[cropType] || 0;
          const newMoney = this.money + (price * amount);
          const newInventory = { ...this.inventory, [cropType]: this.inventory[cropType] - amount };
          setGameState(prev => ({
            ...prev,
            money: newMoney,
            inventory: newInventory
          }));
          this.money = newMoney;
          this.inventory = newInventory;
          this.print(`Successfully sold ${amount} ${cropType}`);
          return true;
        }
        this.print(`Failed to sell ${amount} ${cropType}`);
        return false;
      },
      
      buy: function(cropType, amount) {
        const prices = { wheat: 10, corn: 15, potato: 20 };
        const price = (prices[cropType] || 0) * amount;
        if (this.money >= price) {
          const newMoney = this.money - price;
          const newInventory = {
            ...this.inventory,
            [`${cropType}_seeds`]: (this.inventory[`${cropType}_seeds`] || 0) + amount
          };
          setGameState(prev => ({
            ...prev,
            money: newMoney,
            inventory: newInventory
          }));
          this.money = newMoney;
          this.inventory = newInventory;
          this.print(`Successfully bought ${amount} ${cropType} seeds`);
          return true;
        }
        this.print(`Failed to buy ${amount} ${cropType} seeds`);
        return false;
      },
      
      print: function(text) {
        setOutput(prev => prev + text + '\n');
      },
      
      toString: function() {
        return `Farm(money=$${this.money}, size=${this.size}x${this.size}, inventory=${JSON.stringify(this.inventory)})`;
      }
    };
    
    setFarm(newFarm);
    setOutput(prev => prev + `Farm initialized: ${newFarm.toString()}\n`);
  }, [gameState, setGameState, consoleId]);

  // Save console state when it changes
  useEffect(() => {
    consoleStates[consoleId] = {
      input,
      output,
      farm,
      isPlaying,
      currentLine
    };
  }, [input, output, farm, isPlaying, currentLine, consoleId]);

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && e.ctrlKey) {
      e.preventDefault();
      togglePlayPause();
    }
  };

  const executeLine = (line) => {
    if (!farm) return;

    try {
      setOutput(prev => prev + '\n>>> ' + line + '\n');

      // Create a function that returns a Promise to handle async operations
      const executeCode = new Function('farm', `
        return new Promise((resolve, reject) => {
          try {
            const result = eval(${JSON.stringify(line)});
            if (result instanceof Promise) {
              result.then(resolve).catch(reject);
            } else {
              resolve(result);
            }
          } catch (error) {
            farm.print('Error: ' + error.message);
            reject(error);
          }
        });
      `);

      return executeCode(farm);
    } catch (error) {
      setOutput(prev => prev + 'Error: ' + error.message + '\n');
      return Promise.reject(error);
    }
  };

  const togglePlayPause = async () => {
    if (!input.trim() || !farm) return;

    if (!isPlaying) {
      // Start playing
      setIsPlaying(true);
      try {
        // Execute the entire code at once
        const result = await executeLine(input);
        if (result !== undefined) {
          setOutput(prev => prev + result + '\n');
        }
      } catch (error) {
        console.error('Execution error:', error);
      } finally {
        setIsPlaying(false);
      }
    }
  };

  // Cleanup interval on unmount
  useEffect(() => {
    return () => {
      if (executionInterval.current) {
        clearInterval(executionInterval.current);
      }
    };
  }, []);

  return (
    <ConsoleContainer>
      <ConsoleOutput>
        {output}
      </ConsoleOutput>
      <ConsoleInput
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Enter JavaScript code here..."
        disabled={!farm || isPlaying}
      />
      <PlayPauseButton 
        onClick={togglePlayPause} 
        disabled={!farm || !input.trim()}
      >
        {isPlaying ? <PauseIcon /> : <PlayIcon />}
      </PlayPauseButton>
    </ConsoleContainer>
  );
}

export default JavaScriptConsole;
