import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const ConsoleContainer = styled.div`
  background-color: #1e1e1e;
  border-radius: 5px;
  padding: 10px;
  margin: 10px;
  height: 300px;
  display: flex;
  flex-direction: column;
`;

const ConsoleOutput = styled.div`
  color: #ffffff;
  font-family: monospace;
  flex-grow: 1;
  overflow-y: auto;
  padding: 10px;
  white-space: pre-wrap;
  background-color: #2d2d2d;
  margin-bottom: 10px;
  border-radius: 3px;
`;

const ConsoleInput = styled.textarea`
  background-color: #2d2d2d;
  color: #ffffff;
  font-family: monospace;
  border: 1px solid #3f3f3f;
  border-radius: 3px;
  padding: 5px;
  resize: none;
  height: 60px;
`;

const RunButton = styled.button`
  background-color: #4CAF50;
  color: white;
  padding: 8px 15px;
  border: none;
  border-radius: 3px;
  margin-top: 5px;
  cursor: pointer;
  &:hover {
    background-color: #45a049;
  }
`;

function JavaScriptConsole({ gameState, setGameState }) {
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('Welcome to the JavaScript Console!\nType your JavaScript code and press Run or Ctrl+Enter\n');
  const [farm, setFarm] = useState(null);

  // Initialize farm when component mounts
  useEffect(() => {
    // Start with 1x1 land
    const initialLand = Array(1).fill().map(() => ({
      isPlowed: false,
      hasCrop: false,
      cropType: null,
      cropState: null,
      plantedTime: null
    }));

    const newFarm = {
      money: gameState.money,
      inventory: gameState.inventory,
      land: initialLand,
      size: 1, // Current size of the farm (1x1, 2x2, etc.)
      maxSize: 5, // Maximum allowed size
      drone: { x: 0, y: 0 },
      
      expand: function() {
        if (this.size >= this.maxSize) {
          this.print(`Farm is already at maximum size (${this.maxSize}x${this.maxSize})`);
          return false;
        }

        const newSize = this.size + 1;
        const newLand = Array(newSize * newSize).fill().map((_, index) => {
          // Keep existing tiles
          if (index < this.land.length) {
            return this.land[index];
          }
          // Add new empty tiles
          return {
            isPlowed: false,
            hasCrop: false,
            cropType: null,
            cropState: null,
            plantedTime: null
          };
        });

        setGameState(prev => ({ ...prev, land: newLand }));
        this.land = newLand;
        this.size = newSize;
        this.print(`Farm expanded to ${newSize}x${newSize}`);
        return true;
      },
      
      plow: function(x, y) {
        console.log("Calling plow", x, y);
        if (x >= 0 && x < this.size && y >= 0 && y < this.size) {
          const newLand = [...this.land];
          newLand[y * this.size + x] = { ...newLand[y * this.size + x], isPlowed: true };
          setGameState(prev => ({ ...prev, land: newLand }));
          this.land = newLand;
          this.print(`Successfully plowed tile at (${x}, ${y})`);
          return true;
        }
        this.print(`Failed to plow tile at (${x}, ${y}) - coordinates out of bounds`);
        return false;
      },
      
      plant: function(x, y, cropType) {
        console.log("Calling plant", x, y, cropType);
        if (x >= 0 && x < this.size && y >= 0 && y < this.size) {
          const index = y * this.size + x;
          if (this.land[index].isPlowed && !this.land[index].hasCrop) {
            const newLand = [...this.land];
            newLand[index] = {
              ...newLand[index],
              hasCrop: true,
              cropType: cropType,
              cropState: 'growing',
              plantedTime: Date.now()
            };
            setGameState(prev => ({ ...prev, land: newLand }));
            this.land = newLand;
            this.print(`Successfully planted ${cropType} at (${x}, ${y})`);
            return true;
          }
        }
        this.print(`Failed to plant ${cropType} at (${x}, ${y})`);
        return false;
      },
      
      harvest: function(x, y) {
        if (x >= 0 && x < this.size && y >= 0 && y < this.size) {
          const index = y * this.size + x;
          if (this.land[index].hasCrop && this.land[index].cropState === 'ready') {
            const cropType = this.land[index].cropType;
            const newLand = [...this.land];
            newLand[index] = {
              ...newLand[index],
              hasCrop: false,
              cropType: null,
              cropState: null,
              plantedTime: null
            };
            const newInventory = { ...this.inventory, [cropType]: (this.inventory[cropType] || 0) + 1 };
            setGameState(prev => ({
              ...prev,
              land: newLand,
              inventory: newInventory
            }));
            this.land = newLand;
            this.inventory = newInventory;
            this.print(`Successfully harvested crop at (${x}, ${y})`);
            return true;
          }
        }
        this.print(`Failed to harvest crop at (${x}, ${y})`);
        return false;
      },
      
      isCropReady: function(x, y) {
        if (x >= 0 && x < this.size && y >= 0 && y < this.size) {
          const tile = this.land[y * this.size + x];
          return tile.hasCrop && tile.cropState === 'ready';
        }
        return false;
      },
      
      sellCrop: function(cropType, amount) {
        const available = this.inventory[cropType] || 0;
        if (available >= amount) {
          const prices = { wheat: 20, corn: 30, potato: 40 };
          const price = prices[cropType] || 0;
          const newMoney = this.money + (price * amount);
          const newInventory = { ...this.inventory, [cropType]: this.inventory[cropType] - amount };
          setGameState(prev => ({
            ...prev,
            money: newMoney,
            inventory: newInventory
          }));
          this.money = newMoney;
          this.inventory = newInventory;
          this.print(`Successfully sold ${amount} ${cropType}`);
          return true;
        }
        this.print(`Failed to sell ${amount} ${cropType}`);
        return false;
      },
      
      buySeeds: function(cropType, amount) {
        const prices = { wheat: 10, corn: 15, potato: 20 };
        const price = (prices[cropType] || 0) * amount;
        if (this.money >= price) {
          const newMoney = this.money - price;
          const newInventory = {
            ...this.inventory,
            [`${cropType}_seeds`]: (this.inventory[`${cropType}_seeds`] || 0) + amount
          };
          setGameState(prev => ({
            ...prev,
            money: newMoney,
            inventory: newInventory
          }));
          this.money = newMoney;
          this.inventory = newInventory;
          this.print(`Successfully bought ${amount} ${cropType} seeds`);
          return true;
        }
        this.print(`Failed to buy ${amount} ${cropType} seeds`);
        return false;
      },
      
      print: function(text) {
        setOutput(prev => prev + text + '\n');
      },
      
      toString: function() {
        return `Farm(money=$${this.money}, size=${this.size}x${this.size}, inventory=${JSON.stringify(this.inventory)})`;
      },

      move_drone: function(x, y) {
        if (0 <= x && x < this.size && 0 <= y && y < this.size) {
          this.drone.x = x;
          this.drone.y = y;
          this.print(`Moved drone to (${x}, ${y})`);
          return true;
        }
        this.print("Invalid coordinates");
        return false;
      },

      drone_plow: function() {
        return this.plow(this.drone.x, this.drone.y);
      },

      drone_plant: function(cropType) {
        return this.plant(this.drone.x, this.drone.y, cropType);
      },

      drone_harvest: function() {
        return this.harvest(this.drone.x, this.drone.y);
      },

      drone_scan: function() {
        const x = this.drone.x;
        const y = this.drone.y;
        const index = y * this.size + x;
        const tile = this.land[index];
        
        let status = "empty";
        if (tile.isPlowed) {
          status = "plowed";
        }
        if (tile.hasCrop) {
          status = `${tile.cropState} ${tile.cropType}`;
        }
        
        this.print(`Scan results at (${x}, ${y}): ${status}`);
        return status;
      },

      get_drone_position: function() {
        return { x: this.drone.x, y: this.drone.y };
      }
    };
    
    setFarm(newFarm);
    setOutput(prev => prev + `Farm initialized: ${newFarm.toString()}\n`);
  }, [gameState, setGameState]);

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && e.ctrlKey) {
      e.preventDefault();
      handleRun();
    }
  };

  const handleRun = () => {
    if (!input.trim() || !farm) return;

    try {
      // Add the input to the output history
      setOutput(prev => prev + '\n>>> ' + input + '\n');

      // Create a function that has access to the farm object
      const executeCode = new Function('farm', `
        try {
          ${input}
        } catch (error) {
          farm.print('Error: ' + error.message);
        }
      `);

      // Execute the code with the farm object
      executeCode(farm);
      
      // Clear the input
      setInput('');
    } catch (error) {
      setOutput(prev => prev + 'Error: ' + error.message + '\n');
    }
  };

  return (
    <ConsoleContainer>
      <ConsoleOutput>
        {output}
      </ConsoleOutput>
      <ConsoleInput
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Enter JavaScript code here..."
        disabled={!farm}
      />
      <RunButton onClick={handleRun} disabled={!farm}>
        Run
      </RunButton>
    </ConsoleContainer>
  );
}

export default JavaScriptConsole;
