import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const ConsoleContainer = styled.div`
  background-color: #1e1e1e;
  border-radius: 5px;
  padding: 10px;
  margin: 10px;
  height: 300px;
  display: flex;
  flex-direction: column;
`;

const ConsoleOutput = styled.div`
  color: #ffffff;
  font-family: monospace;
  flex-grow: 1;
  overflow-y: auto;
  padding: 10px;
  white-space: pre-wrap;
  background-color: #2d2d2d;
  margin-bottom: 10px;
  border-radius: 3px;
`;

const ConsoleInput = styled.textarea`
  background-color: #2d2d2d;
  color: #ffffff;
  font-family: monospace;
  border: 1px solid #3f3f3f;
  border-radius: 3px;
  padding: 5px;
  resize: none;
  height: 60px;
`;

const RunButton = styled.button`
  background-color: #4CAF50;
  color: white;
  padding: 8px 15px;
  border: none;
  border-radius: 3px;
  margin-top: 5px;
  cursor: pointer;
  &:hover {
    background-color: #45a049;
  }
`;

function PythonConsole({ gameState, setGameState }) {
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('Welcome to the Python Console!\nType your Python code and press Run or Ctrl+Enter\n');
  const initScriptRef = useRef(null);

  useEffect(() => {
    // Add the farm actions to the window object
    window.farm_action = (action, ...args) => {
      try {
        switch (action) {
          case 'plow':
            const [x, y] = args;
