              const newLand = [...gameState.land];
              newLand[y * 5 + x] = { ...newLand[y * 5 + x], isPlowed: true };
              setGameState(prev => ({ ...prev, land: newLand }));
              return true;
            }
            return false;

          case 'plant':
            const [plantX, plantY, cropType] = args;
            if (plantX >= 0 && plantX < 5 && plantY >= 0 && plantY < 5) {
              const index = plantY * 5 + plantX;
              if (gameState.land[index].isPlowed && !gameState.land[index].hasCrop) {
                const newLand = [...gameState.land];
                newLand[index] = {
                  ...newLand[index],
                  hasCrop: true,
                  cropType: cropType,
                  cropState: 'growing',
                  plantedTime: Date.now()
                };
                setGameState(prev => ({ ...prev, land: newLand }));
                return true;
              }
            }
            return false;

          case 'harvest':
            const [harvestX, harvestY] = args;
            if (harvestX >= 0 && harvestX < 5 && harvestY >= 0 && harvestY < 5) {
              const index = harvestY * 5 + harvestX;
              if (gameState.land[index].hasCrop && gameState.land[index].cropState === 'ready') {
                const cropType = gameState.land[index].cropType;
                const newLand = [...gameState.land];
                newLand[index] = {
                  ...newLand[index],
                  hasCrop: false,
                  cropType: null,
                  cropState: null,
                  plantedTime: null
                };
                setGameState(prev => ({
                  ...prev,
                  land: newLand,
                  inventory: {
                    ...prev.inventory,
                    [cropType]: (prev.inventory[cropType] || 0) + 1
                  }
                }));
                return true;
              }
            }
            return false;

          case 'is_crop_ready':
            const [checkX, checkY] = args;
            if (checkX >= 0 && checkX < 5 && checkY >= 0 && checkY < 5) {
              const tile = gameState.land[checkY * 5 + checkX];
              return tile.hasCrop && tile.cropState === 'ready';
            }
            return false;

          case 'sell_crop':
            const [sellCropType, amount] = args;
            const available = gameState.inventory[sellCropType] || 0;
            if (available >= amount) {
              const prices = { wheat: 20, corn: 30, potato: 40 };
