from browser import window, console
import sys

# Create the Farm class
class Farm:
    def __init__(self):
        self.money = ${gameState.money}
        self.inventory = ${JSON.stringify(gameState.inventory)}
        self.land_size = 5
    
    def plow(self, x, y):
        console.log("Calling plow", x, y)
        result = window.farm_action("plow", x, y)
        console.log("Plow result:", result)
        return result
    
    def plant(self, x, y, crop_type):
        console.log("Calling plant", x, y, crop_type)
        result = window.farm_action("plant", x, y, crop_type)
        console.log("Plant result:", result)
        return result
    
    def harvest(self, x, y):
        return window.farm_action("harvest", x, y)
    
    def is_crop_ready(self, x, y):
        return window.farm_action("is_crop_ready", x, y)
    
    def sell_crop(self, crop_type, amount):
        return window.farm_action("sell_crop", crop_type, amount)
    
    def buy_seeds(self, crop_type, amount):
        return window.farm_action("buy_seeds", crop_type, amount)
    
    def __str__(self):
        return "Farm(money=$%d, inventory=%s)" % (self.money, str(self.inventory))

# Create global farm instance
farm = Farm()
print("Farm initialized:", farm)

# Override print function
def print(*args, sep=" ", end="\\n"):
    text = sep.join(str(arg) for arg in args) + end
    window.update_console(text)

# Make our print function global
__builtins__.print = print
