    document.body.appendChild(script);
    initScriptRef.current = script;

    // Clean up
    return () => {
      if (initScriptRef.current) {
        document.body.removeChild(initScriptRef.current);
      }
    };
  }, [gameState, setGameState]);

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && e.ctrlKey) {
      e.preventDefault();
      handleRun();
    }
  };

  const handleRun = () => {
    if (!input.trim()) return;

    try {
      // Add the input to the output history
      setOutput(prev => prev + '\n>>> ' + input + '\n');

      // Create a script element for the input code
      const script = document.createElement('script');
      script.type = 'text/python';
      script.innerHTML = input;
      document.body.appendChild(script);

      // Remove the script element after a short delay to ensure execution
      setTimeout(() => {
        document.body.removeChild(script);
      }, 100);
      
      // Clear the input
      setInput('');
    } catch (error) {
      setOutput(prev => prev + 'Error: ' + error + '\n');
    }
  };

  return (
    <ConsoleContainer>
      <ConsoleOutput>
        {output}
      </ConsoleOutput>
      <ConsoleInput
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Enter Python code here..."
      />
      <RunButton onClick={handleRun}>Run</RunButton>
    </ConsoleContainer>
  );
}

export default PythonConsole;
