import React, { useRef, useEffect, useState } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';

const Game3D = ({ gameState, setGameState }) => {
  const [isInitialized, setIsInitialized] = useState(false);
  const canvasRef = useRef(null);
  const sceneRef = useRef(null);
  const cameraRef = useRef(null);
  const rendererRef = useRef(null);
  const modelsRef = useRef({});
  const droneRef = useRef(null);
  const animationFrameRef = useRef(null);

  // Initialize Three.js scene
  const initializeScene = () => {
    try {
      if (!canvasRef.current) {
        console.warn('Canvas element not found');
        return false;
      }

      // Create scene
      const scene = new THREE.Scene();
      scene.background = new THREE.Color(0x87CEEB);
      
      // Create camera
      const camera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
      camera.position.set(5, 5, 5);
      camera.lookAt(0, 0, 0);
      
      // Create renderer
      const renderer = new THREE.WebGLRenderer({ 
        canvas: canvasRef.current,
        antialias: true,
        alpha: true
      });
      renderer.setSize(600, 600, false);

      // Add lights
      const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
      scene.add(ambientLight);
      
      const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
      directionalLight.position.set(10, 10, 5);
      scene.add(directionalLight);

      // Create ground plane
      const groundGeometry = new THREE.PlaneGeometry(5, 5);
      const groundMaterial = new THREE.MeshStandardMaterial({ 
        color: 0x8B4513,
        roughness: 0.8,
        metalness: 0.2
      });
      const ground = new THREE.Mesh(groundGeometry, groundMaterial);
      ground.rotation.x = -Math.PI / 2;
      scene.add(ground);

      // Store references
      sceneRef.current = scene;
      cameraRef.current = camera;
      rendererRef.current = renderer;
      modelsRef.current = {};

      return true;
    } catch (error) {
      console.error('Error initializing scene:', error);
      return false;
    }
  };

  // Load 3D models
  const loadModels = async () => {
    try {
      const loader = new GLTFLoader();
      const models = {};

      // Load drone model
      await new Promise((resolve, reject) => {
        loader.load(
          '/models/drone.glb',
          (gltf) => {
            const drone = gltf.scene;
            drone.scale.set(0.5, 0.5, 0.5);
            drone.position.set(0, 0.5, 0);
            sceneRef.current.add(drone);
            droneRef.current = drone;
            models.drone = drone;
            resolve();
          },
          undefined,
          reject
        );
      });

      // Load crop models
      await Promise.all(['wheat', 'corn', 'potato'].map(crop => 
        new Promise((resolve, reject) => {
          loader.load(
            `/models/${crop}.glb`,
            (gltf) => {
              const model = gltf.scene;
              model.scale.set(0.3, 0.3, 0.3);
              model.visible = false;
              sceneRef.current.add(model);
              models[crop] = model;
              resolve();
            },
            undefined,
            reject
          );
        })
      ));

      modelsRef.current = models;
      return true;
    } catch (error) {
      console.error('Error loading models:', error);
      return false;
    }
  };

  // Animation loop
  const startAnimation = () => {
    const animate = () => {
      animationFrameRef.current = requestAnimationFrame(animate);
      
      if (droneRef.current) {
        droneRef.current.rotation.y += 0.01;
        droneRef.current.position.y = Math.sin(Date.now() * 0.001) * 0.1 + 0.5;
      }

      rendererRef.current.render(sceneRef.current, cameraRef.current);
    };
    animate();
  };

  // Initialize everything
  useEffect(() => {
    const initialize = async () => {
      if (isInitialized) return;

      const sceneInitialized = initializeScene();
      if (!sceneInitialized) return;

      const modelsLoaded = await loadModels();
      if (!modelsLoaded) return;

      startAnimation();
      setIsInitialized(true);
    };

    initialize();

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
      if (rendererRef.current) {
        rendererRef.current.dispose();
      }
    };
  }, [isInitialized]);

  // Update game state
  useEffect(() => {
    if (!isInitialized) return;

    if (droneRef.current) {
      droneRef.current.position.set(
        gameState.dronePosition.x - 2,
        0.5,
        gameState.dronePosition.y - 2
      );
    }

    gameState.grid.forEach((row, x) => {
      row.forEach((cell, y) => {
        if (cell && modelsRef.current[cell.type]) {
          const model = modelsRef.current[cell.type];
          model.position.set(x - 2, 0, y - 2);
          model.visible = true;
        }
      });
    });
  }, [gameState, isInitialized]);

  return (
    <div style={{ width: '100%', height: '100%', position: 'relative' }}>
      <canvas 
        ref={canvasRef} 
        style={{ 
          width: '100%', 
          height: '100%',
          display: 'block'
        }} 
      />
    </div>
  );
};

export default Game3D;
