import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/addons/controls/OrbitControls.js';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader.js';
import { OBJLoader } from 'three/addons/loaders/OBJLoader.js';
import { EffectComposer } from 'three/addons/postprocessing/EffectComposer.js';
import { RenderPass } from 'three/addons/postprocessing/RenderPass.js';
import { UnrealBloomPass } from 'three/addons/postprocessing/UnrealBloomPass.js';
import { SMAAPass } from 'three/addons/postprocessing/SMAAPass.js';
import { Sky } from 'three/addons/objects/Sky.js';

const Game3D = ({ gameState }) => {
  const canvasRef = useRef(null);
  const sceneRef = useRef(null);
  const droneRef = useRef(null);
  const composerRef = useRef(null);
  const modelsRef = useRef({});
  const texturesRef = useRef({});

  // Convert game coordinates to scene coordinates
  const gameToScene = (x, y) => {
    return {
      x: x - 2.5,
      z: y - 2.5
    };
  };

  // Load textures
  const loadTextures = async () => {
    const textureLoader = new THREE.TextureLoader();
    const loadTexture = (url) => new Promise((resolve, reject) => {
      textureLoader.load(url, resolve, undefined, reject);
    });

    try {
      texturesRef.current = {
        grass: {
          diffuse: await loadTexture('/textures/grass.jpg')
        },
        soil: {
          diffuse: await loadTexture('/textures/plowed_soil.jpg')
        }
      };

      // Configure texture properties
      Object.values(texturesRef.current).forEach(textures => {
        Object.values(textures).forEach(texture => {
          texture.wrapS = texture.wrapT = THREE.RepeatWrapping;
          texture.repeat.set(2, 2);
        });
      });
    } catch (error) {
      console.error('Error loading textures:', error);
    }
  };

  // Load models
  const loadModels = async () => {
    const gltfLoader = new GLTFLoader();
    const objLoader = new OBJLoader();

    const loadGLTF = (url) => new Promise((resolve, reject) => {
      gltfLoader.load(url, resolve, undefined, reject);
    });

    const loadOBJ = (url) => new Promise((resolve, reject) => {
      objLoader.load(url, resolve, undefined, reject);
    });

    try {
      // Load drone model
      const droneModel = await loadGLTF('/models/drone.glb');
      modelsRef.current.drone = droneModel.scene;

      // Load crop models
      modelsRef.current.wheat = await loadOBJ('/models/wheat.obj');
      modelsRef.current.corn = await loadOBJ('/models/corn.obj');
      modelsRef.current.potato = await loadOBJ('/models/potato.obj');

      // Configure models
      Object.values(modelsRef.current).forEach(model => {
        model.traverse(child => {
          if (child.isMesh) {
            child.castShadow = true;
            child.receiveShadow = true;
          }
        });
      });
    } catch (error) {
      console.error('Error loading models:', error);
    }
  };

  // Create environment map
  const createEnvironmentMap = (renderer) => {
    const pmremGenerator = new THREE.PMREMGenerator(renderer);
    pmremGenerator.compileEquirectangularShader();

    const sky = new Sky();
    sky.scale.setScalar(450000);
    
    const sun = new THREE.Vector3();
    const uniforms = sky.material.uniforms;
    uniforms['turbidity'].value = 10;
    uniforms['rayleigh'].value = 3;
    uniforms['mieCoefficient'].value = 0.005;
    uniforms['mieDirectionalG'].value = 0.7;

    const phi = THREE.MathUtils.degToRad(90 - 2);
    const theta = THREE.MathUtils.degToRad(180);
    sun.setFromSphericalCoords(1, phi, theta);
    uniforms['sunPosition'].value.copy(sun);

    const renderTarget = pmremGenerator.fromScene(sky);
    pmremGenerator.dispose();

    return renderTarget.texture;
  };

  // Create fallback drone model
  const createFallbackDrone = () => {
    const droneGroup = new THREE.Group();

    // Drone body
    const bodyGeometry = new THREE.BoxGeometry(0.4, 0.1, 0.4);
    const bodyMaterial = new THREE.MeshStandardMaterial({
      color: 0x3498db,
      metalness: 0.8,
      roughness: 0.2
    });
    const body = new THREE.Mesh(bodyGeometry, bodyMaterial);
    body.castShadow = true;
    droneGroup.add(body);

    // Drone arms
    const armGeometry = new THREE.CylinderGeometry(0.02, 0.02, 0.3);
    const armMaterial = new THREE.MeshStandardMaterial({
      color: 0x2c3e50,
      metalness: 0.5,
      roughness: 0.5
    });

    // Create four arms
    for (let i = 0; i < 4; i++) {
      const arm = new THREE.Mesh(armGeometry, armMaterial);
      arm.position.y = 0.05;
      arm.rotation.z = Math.PI / 2;
      arm.rotation.y = (i * Math.PI) / 2;
      arm.position.x = Math.cos(arm.rotation.y) * 0.2;
      arm.position.z = Math.sin(arm.rotation.y) * 0.2;
      arm.castShadow = true;
      droneGroup.add(arm);
    }

    // Propellers
    const propGeometry = new THREE.BoxGeometry(0.05, 0.01, 0.3);
    const propMaterial = new THREE.MeshStandardMaterial({
      color: 0x95a5a6,
      metalness: 0.3,
      roughness: 0.7
    });

    // Create four propellers
    for (let i = 0; i < 4; i++) {
      const propeller = new THREE.Group();
      
      const blade1 = new THREE.Mesh(propGeometry, propMaterial);
      const blade2 = new THREE.Mesh(propGeometry, propMaterial);
      blade2.rotation.y = Math.PI / 2;
      
      propeller.add(blade1, blade2);
      propeller.position.y = 0.1;
      propeller.position.x = Math.cos(i * Math.PI / 2) * 0.2;
      propeller.position.z = Math.sin(i * Math.PI / 2) * 0.2;
      propeller.castShadow = true;
      
      droneGroup.add(propeller);
    }

    return droneGroup;
  };

  // Create and update tiles
  const updateTiles = () => {
    if (!sceneRef.current || !texturesRef.current) return;

    // Remove old tiles
    const oldTiles = sceneRef.current.children.filter(child => child.userData.isTile);
    oldTiles.forEach(tile => {
      sceneRef.current.remove(tile);
      if (tile.geometry) tile.geometry.dispose();
      if (tile.material) tile.material.dispose();
    });

    // Create new tiles based on gameState
    const size = Math.sqrt(gameState.land.length);
    const tileSize = 1;
    const offset = (size - 1) / 2;

    gameState.land.forEach((tile, index) => {
      if (tile.isPlowed) {
        const x = Math.floor(index / size);
        const z = index % size;
        
        const geometry = new THREE.PlaneGeometry(tileSize * 0.9, tileSize * 0.9);
        const material = new THREE.MeshStandardMaterial({
          map: texturesRef.current.soil.diffuse,
          roughness: 0.8,
          metalness: 0.2
        });
        
        const mesh = new THREE.Mesh(geometry, material);
        mesh.rotation.x = -Math.PI / 2;
        mesh.position.set(x - offset, 0.01, z - offset); // Slightly above ground
        mesh.receiveShadow = true;
        mesh.userData.isTile = true;
        
        sceneRef.current.add(mesh);
      }
    });
  };

  useEffect(() => {
    if (!canvasRef.current) return;

    // Create scene
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x87CEEB);
    scene.fog = new THREE.FogExp2(0x87CEEB, 0.01);
    sceneRef.current = scene;

    // Create camera
    const camera = new THREE.PerspectiveCamera(60, 1, 0.1, 1000);
    camera.position.set(8, 12, 8);
    camera.lookAt(0, 0, 0);

    // Create renderer with improved settings
    const renderer = new THREE.WebGLRenderer({
      canvas: canvasRef.current,
      antialias: true,
      powerPreference: "high-performance",
      stencil: false
    });
    renderer.setSize(600, 600);
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    renderer.outputColorSpace = THREE.SRGBColorSpace;
    renderer.toneMapping = THREE.ACESFilmicToneMapping;
    renderer.toneMappingExposure = 1.0;
    renderer.setPixelRatio(window.devicePixelRatio);

    // Create environment map
    const envMap = createEnvironmentMap(renderer);
    scene.environment = envMap;

    // Add lights with improved settings
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
    scene.add(ambientLight);

    const sunLight = new THREE.DirectionalLight(0xffffff, 2.0);
    sunLight.position.set(10, 10, 5);
    sunLight.castShadow = true;
    sunLight.shadow.mapSize.width = 2048;
    sunLight.shadow.mapSize.height = 2048;
    sunLight.shadow.camera.near = 0.5;
    sunLight.shadow.camera.far = 50;
    sunLight.shadow.camera.left = -10;
    sunLight.shadow.camera.right = 10;
    sunLight.shadow.camera.top = 10;
    sunLight.shadow.camera.bottom = -10;
    sunLight.shadow.bias = -0.001;
    scene.add(sunLight);

    // Add subtle point lights for better ambiance
    const pointLight1 = new THREE.PointLight(0xffd700, 0.5, 10);
    pointLight1.position.set(2, 2, 2);
    scene.add(pointLight1);

    const pointLight2 = new THREE.PointLight(0x00ffff, 0.3, 10);
    pointLight2.position.set(-2, 3, -2);
    scene.add(pointLight2);

    // Create ground
    const groundGeometry = new THREE.PlaneGeometry(10, 10);
    const groundMaterial = new THREE.MeshStandardMaterial({
      map: texturesRef.current.grass.diffuse,
      roughness: 0.8,
      metalness: 0.2
    });
    const ground = new THREE.Mesh(groundGeometry, groundMaterial);
    ground.rotation.x = -Math.PI / 2;
    ground.receiveShadow = true;
    scene.add(ground);

    // Add grid helper with improved visibility
    const gridHelper = new THREE.GridHelper(5, 5, 0x000000, 0x000000);
    gridHelper.position.y = 0.01;
    gridHelper.material.opacity = 0.2;
    gridHelper.material.transparent = true;
    scene.add(gridHelper);

    // Setup post-processing with improved settings
    const composer = new EffectComposer(renderer);
    composerRef.current = composer;

    const renderPass = new RenderPass(scene, camera);
    composer.addPass(renderPass);

    const bloomPass = new UnrealBloomPass(
      new THREE.Vector2(600, 600),
      0.5,  // strength
      0.4,  // radius
      0.85  // threshold
    );
    composer.addPass(bloomPass);

    const smaaPass = new SMAAPass(600, 600);
    composer.addPass(smaaPass);

    // Add OrbitControls with improved settings
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;
    controls.screenSpacePanning = false;
    controls.minDistance = 5;
    controls.maxDistance = 20;
    controls.maxPolarAngle = Math.PI / 2.1;
    controls.minPolarAngle = Math.PI / 6;
    controls.enableZoom = true;
    controls.zoomSpeed = 0.5;
    controls.rotateSpeed = 0.5;

    // Set initial camera position for isometric view
    camera.position.set(8, 12, 8);
    controls.target.set(0, 0, 0);
    controls.update();

    // Load assets with improved error handling
    Promise.all([loadTextures(), loadModels()]).then(() => {
      // Add drone to scene with improved fallback handling
      if (modelsRef.current.drone) {
        const drone = modelsRef.current.drone.clone();
        drone.scale.set(0.5, 0.5, 0.5);
        drone.traverse((child) => {
          if (child.isMesh) {
            child.castShadow = true;
            child.material.envMapIntensity = 1;
          }
        });
        scene.add(drone);
        droneRef.current = drone;
      } else {
        console.log('Using fallback drone model');
        const fallbackDrone = createFallbackDrone();
        scene.add(fallbackDrone);
        droneRef.current = fallbackDrone;
      }
    }).catch(error => {
      console.error('Error loading assets:', error);
      const fallbackDrone = createFallbackDrone();
      scene.add(fallbackDrone);
      droneRef.current = fallbackDrone;
    });

    // Animation loop with improved timing
    let lastTime = 0;
    const animate = (time) => {
      requestAnimationFrame(animate);

      const delta = (time - lastTime) / 1000;
      lastTime = time;

      // Update drone position and animation with smooth interpolation
      if (droneRef.current) {
        const targetPos = gameToScene(gameState.dronePosition.x, gameState.dronePosition.y);
        droneRef.current.position.x = THREE.MathUtils.lerp(
          droneRef.current.position.x,
          targetPos.x,
          5 * delta
        );
        droneRef.current.position.z = THREE.MathUtils.lerp(
          droneRef.current.position.z,
          targetPos.z,
          5 * delta
        );
        droneRef.current.position.y = 0.5 + Math.sin(time * 0.002) * 0.1;
        
        // Rotate propellers if using fallback model
        if (!modelsRef.current.drone) {
          droneRef.current.children.forEach((child, index) => {
            if (index > 4) { // Skip body and arms
              child.rotation.y += 15 * delta;
            }
          });
        }
      }

      // Update controls and render
      controls.update();
      composer.render();
    };
    animate(0);

    // Cleanup with improved resource disposal
    return () => {
      renderer.dispose();
      composer.dispose();
      controls.dispose();
      envMap.dispose();
      
      Object.values(texturesRef.current).forEach(textures => {
        Object.values(textures).forEach(texture => texture.dispose());
      });
      
      scene.traverse((object) => {
        if (object.geometry) object.geometry.dispose();
        if (object.material) {
          if (Array.isArray(object.material)) {
            object.material.forEach(material => material.dispose());
          } else {
            object.material.dispose();
          }
        }
      });
    };
  }, []);

  // Update drone position when gameState changes
  useEffect(() => {
    if (droneRef.current && gameState.dronePosition) {
      const targetPos = gameToScene(gameState.dronePosition.x, gameState.dronePosition.y);
      droneRef.current.position.x = targetPos.x;
      droneRef.current.position.z = targetPos.z;
    }
  }, [gameState.dronePosition]);

  // Update tiles when gameState changes
  useEffect(() => {
    updateTiles();
  }, [gameState.land]);

  return (
    <div style={{ 
      width: '600px', 
      height: '600px', 
      backgroundColor: '#000000',
      margin: '0 auto',
      borderRadius: '8px',
      overflow: 'hidden',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
    }}>
      <canvas 
        ref={canvasRef}
        style={{
          width: '100%',
          height: '100%'
        }}
      />
    </div>
  );
};

export default Game3D;
